class HashTable:
    def __init__(self, size=10):
        self.size = size
        self.table = [[] for _ in range(size)]

    def _hash(self, key):
        return hash(key) % self.size

    def insert(self, key, value):
        hash_key = self._hash(key)
        # Handle collisions with chaining
        for item in self.table[hash_key]:
            if item[0] == key:
                item[1] = value
                return
        self.table[hash_key].append([key, value])

    def get(self, key):
        hash_key = self._hash(key)
        for item in self.table[hash_key]:
            if item[0] == key:
                return item[1]
        return None

class TreeNode:
    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

class BST:
    def __init__(self):
        self.root = None

    def insert(self, value):
        if not self.root:
            self.root = TreeNode(value)
        else:
            self._insert_recursive(self.root, value)

    def _insert_recursive(self, node, value):
        if value < node.value:
            if node.left:
                self._insert_recursive(node.left, value)
            else:
                node.left = TreeNode(value)
        else:
            if node.right:
                self._insert_recursive(node.right, value)
            else:
                node.right = TreeNode(value)

# Trie implementation for autocomplete
class TrieNode:
    def __init__(self):
        self.children = {}
        self.is_end = False
        self.frequency = 0

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.is_end = True
        node.frequency += 1

    def search(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                return False
            node = node.children[char]
        return node.is_end

    def autocomplete(self, prefix):
        """Get all words with given prefix"""
        node = self.root
        for char in prefix:
            if char not in node.children:
                return []
            node = node.children[char]

        results = []
        self._collect_words(node, prefix, results)
        return sorted(results, key=lambda x: x[1], reverse=True)[:5]

    def _collect_words(self, node, current, results):
        if node.is_end:
            results.append((current, node.frequency))
        for char, child in node.children.items():
            self._collect_words(child, current + char, results)

from collections import deque, defaultdict

class Graph:
    def __init__(self):
        self.graph = defaultdict(list)

    def add_edge(self, u, v):
        self.graph[u].append(v)

    def bfs(self, start):
        """Breadth-First Search"""
        visited = set([start])
        queue = deque([start])
        result = []

        while queue:
            vertex = queue.popleft()
            result.append(vertex)

            for neighbor in self.graph[vertex]:
                if neighbor not in visited:
                    visited.add(neighbor)
                    queue.append(neighbor)

        return result

    def dfs(self, start, visited=None):
        """Depth-First Search (recursive)"""
        if visited is None:
            visited = set()

        visited.add(start)
        result = [start]

        for neighbor in self.graph[start]:
            if neighbor not in visited:
                result.extend(self.dfs(neighbor, visited))

        return result

    def dijkstra(self, start):
        """Shortest path algorithm"""
        import heapq
        distances = {node: float('inf') for node in self.graph}
        distances[start] = 0
        pq = [(0, start)]

        while pq:
            current_dist, current = heapq.heappop(pq)

            if current_dist > distances[current]:
                continue

            for neighbor, weight in self.graph[current]:
                distance = current_dist + weight

                if distance < distances[neighbor]:
                    distances[neighbor] = distance
                    heapq.heappush(pq, (distance, neighbor))

        return distances

// Generic Repository Pattern
interface Entity {
  id: string;
  createdAt: Date;
  updatedAt: Date;
}

interface Repository<T extends Entity> {
  findById(id: string): Promise<T | null>;
  findAll(filter?: Partial<T>): Promise<T[]>;
  create(data: Omit<T, 'id' | 'createdAt' | 'updatedAt'>): Promise<T>;
  update(id: string, data: Partial<Omit<T, 'id'>>): Promise<T>;
  delete(id: string): Promise<void>;
}

// Concrete implementation
interface User extends Entity {
  email: string;
  name: string;
  role: 'admin' | 'user';
}

class UserRepository implements Repository<User> {
  async findById(id: string): Promise<User | null> {
    // Implementation
    return null;
  }

  async findAll(filter?: Partial<User>): Promise<User[]> {
    // Type-safe filtering!
    return [];
  }

  async create(data: Omit<User, 'id' | 'createdAt' | 'updatedAt'>): Promise<User> {
    // TypeScript ensures we don't accidentally set id or timestamps
    return {} as User;
  }

  async update(id: string, data: Partial<Omit<User, 'id'>>): Promise<User> {
    // Can't accidentally change the id!
    return {} as User;
  }

  async delete(id: string): Promise<void> {}
}

// Type-safe builder ensuring all required fields are set
type BuilderState<T, K extends keyof T> = {
  [P in K]-?: T[P];
} & {
  [P in Exclude<keyof T, K>]?: T[P];
};

interface HttpRequest {
  url: string;        // Required
  method: string;     // Required
  headers?: Record<string, string>;
  body?: unknown;
  timeout?: number;
}

class HttpRequestBuilder<
  K extends keyof HttpRequest = never
> {
  private request: Partial<HttpRequest> = {};

  url(url: string): HttpRequestBuilder<K | 'url'> {
    this.request.url = url;
    return this as any;
  }

  method(method: string): HttpRequestBuilder<K | 'method'> {
    this.request.method = method;
    return this as any;
  }

  headers(headers: Record<string, string>): HttpRequestBuilder<K | 'headers'> {
    this.request.headers = headers;
    return this as any;
  }

  body(body: unknown): HttpRequestBuilder<K | 'body'> {
    this.request.body = body;
    return this as any;
  }

  timeout(timeout: number): HttpRequestBuilder<K | 'timeout'> {
    this.request.timeout = timeout;
    return this as any;
  }

  // Build only works when url AND method are set!
  build(this: HttpRequestBuilder<'url' | 'method'>): HttpRequest {
    return this.request as HttpRequest;
  }
}

