    url: string;
    credentials: {
      username: string;
      password: string;
    };
  };
  cache?: {
    enabled: boolean;
    ttl: number;
  };
}

// All properties (including nested) are optional
type PartialConfig = DeepPartial<Config>;

// All properties (including nested) are readonly
type ImmutableConfig = DeepReadonly<Config>;

// Only string properties
type StringProps = PickByType<Config, string>; // { host: string }

// All except numbers
type NoNumbers = OmitByType<Config, number>;

// Make specific keys required
type ConfigWithRequiredCache = RequireKeys<Config, 'cache'>;

// Method decorator for automatic retry logic
function Retry(maxAttempts: number = 3, delay: number = 1000) {
  return function (
    target: any,
    propertyKey: string,
    descriptor: PropertyDescriptor
  ) {
    const originalMethod = descriptor.value;

    descriptor.value = async function (...args: any[]) {
      let lastError: Error;

      for (let attempt = 1; attempt <= maxAttempts; attempt++) {
        try {
          return await originalMethod.apply(this, args);
        } catch (error) {
          lastError = error as Error;
          console.log(`Attempt ${attempt} failed. Retrying...`);

          if (attempt < maxAttempts) {
            await new Promise(resolve => setTimeout(resolve, delay));
          }
        }
      }

      throw lastError!;
    };

    return descriptor;
  };
}

// Caching decorator
function Memoize(ttl: number = 60000) {
  const cache = new Map<string, { value: any; expiry: number }>();

  return function (
    target: any,
    propertyKey: string,
    descriptor: PropertyDescriptor
  ) {
    const originalMethod = descriptor.value;

    descriptor.value = function (...args: any[]) {
      const key = `${propertyKey}-${JSON.stringify(args)}`;
      const cached = cache.get(key);

      if (cached && cached.expiry > Date.now()) {
        console.log('Cache hit!');
        return cached.value;
      }

      const result = originalMethod.apply(this, args);
      cache.set(key, { value: result, expiry: Date.now() + ttl });

      return result;
    };

    return descriptor;
  };
}

// Usage
class ApiService {
  @Retry(3, 2000)
  @Memoize(30000)
  async fetchUser(id: string): Promise<User> {
    const response = await fetch(`/api/users/${id}`);
    return response.json();
  }
}

type EventMap = {
  'user:created': { id: string; email: string };
  'user:updated': { id: string; changes: Partial<User> };
  'user:deleted': { id: string };
  'order:placed': { orderId: string; userId: string; total: number };
};

class TypedEventEmitter<Events extends Record<string, any>> {
  private listeners: {
    [K in keyof Events]?: Array<(data: Events[K]) => void>;
  } = {};

  on<K extends keyof Events>(
    event: K,
    listener: (data: Events[K]) => void
  ): void {
    if (!this.listeners[event]) {
      this.listeners[event] = [];
    }
    this.listeners[event]!.push(listener);
  }

  emit<K extends keyof Events>(event: K, data: Events[K]): void {
    this.listeners[event]?.forEach(listener => listener(data));
  }

  off<K extends keyof Events>(
    event: K,
    listener: (data: Events[K]) => void
  ): void {
    const eventListeners = this.listeners[event];
    if (eventListeners) {
      this.listeners[event] = eventListeners.filter(l => l !== listener) as any;
    }
  }
}

// Usage - completely type-safe!
const emitter = new TypedEventEmitter<EventMap>();

emitter.on('user:created', (data) => {
  // data is typed as { id: string; email: string }
  console.log(`User created: ${data.email}`);
});

